package arraywhileassignmets;

public class DoubleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double a[]=new double[] {100,200,300,400,500};
		int i=0;

		while(i<a.length)
		{
			System.out.println("Element at Index : "+ i + " " + a[i]);
			i++;
		}
	}


}


