package com.sgtesting.assignmentsloops;

public class CountFrom170To70For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int count=0;
          for(int i=170;i>=70;i--)
          {
        	  if(i%5==0)
        	  {
        		  count++;
        		
        	  }
          }
        	  System.out.println(count);
          
	}

}
