package com.sgtesting.assignmentsloops;

public class MultiplicationBy15DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int n=15;
         int i=1;
         do
         {
        	 int res=n*i;
         	System.out.println(res);
         	i++;
         }while(i<=10);
	}

}
