package com.sgtesting.assignmentsloops;

public class MultiplicationBy15While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=15;
        int i=1;
        while(i<=10)
        {
        	int res=n*i;
        	System.out.println(res);
        	i++;
        }
	}
	

}
